import java.awt.event.*;public class QuizController{QuizModel m;QuizView v;public QuizController(QuizModel m,QuizView v){this.m=m;this.v=v;L();A();v.setVisible(true);}void L(){Question q=m.getCurrentQuestion();v.setQuestion("Q"+(m.getCurrentIndex()+1)+". "+q.getText());String[]o=q.getOptions();for(int i=0;i<4;i++)v.setOption(i,o[i]);v.clearSelection();Integer s=m.getStoredAnswer();if(s!=null)v.setSelected(s);}void A(){v.onNext(e->{S();m.next();L();});v.onPrev(e->{S();m.previous();L();});v.onSubmit(e->{S();int x=m.getScore();javax.swing.JOptionPane.showMessageDialog(v,"Your Score: "+x+"/"+m.getTotalQuestions());});}void S(){Integer x=v.getSelected();if(x!=null)m.setAnswer(x);}}