import java.util.*;public class QuizModel{List<Question>q;Map<Integer,Integer>a=new HashMap<>();int i=0;public QuizModel(List<Question>x){q=x;}public Question getCurrentQuestion(){return q.get(i);}public int getTotalQuestions(){return q.size();}public int getCurrentIndex(){return i;}public void setAnswer(int x){a.put(getCurrentQuestion().getId(),x);}public Integer getStoredAnswer(){return a.get(getCurrentQuestion().getId());}public void next(){if(i<q.size()-1)i++;}public void previous(){if(i>0)i--;}public int getScore(){int s=0;for(Question z:q){Integer y=a.get(z.getId());if(y!=null&&y==z.getCorrectIndex())s++;}return s;}}