import javax.swing.*;import java.awt.*;import java.awt.event.*;public class QuizView extends JFrame{JLabel l=new JLabel();JRadioButton[]o=new JRadioButton[4];ButtonGroup g=new ButtonGroup();JButton p=new JButton("Previous"),n=new JButton("Next"),s=new JButton("Submit");public QuizView(){setTitle("Quiz");setSize(700,350);setDefaultCloseOperation(3);setLocationRelativeTo(null);JPanel m=new JPanel(new BorderLayout());l.setFont(new Font("Arial",1,16));m.add(l,BorderLayout.NORTH);JPanel c=new JPanel(new GridLayout(4,1));for(int i=0;i<4;i++){o[i]=new JRadioButton();o[i].setActionCommand(String.valueOf(i));g.add(o[i]);c.add(o[i]);}m.add(c,BorderLayout.CENTER);JPanel b=new JPanel();b.add(p);b.add(n);b.add(s);m.add(b,BorderLayout.SOUTH);setContentPane(m);}public void setQuestion(String t){l.setText("<html><body style='width:600px'>"+t+"</body></html>");}public void setOption(int i,String t){o[i].setText(t);}public void clearSelection(){g.clearSelection();}public void setSelected(int i){o[i].setSelected(true);}public Integer getSelected(){var m=g.getSelection();return m==null?null:Integer.valueOf(m.getActionCommand());}public void onNext(ActionListener x){n.addActionListener(x);}public void onPrev(ActionListener x){p.addActionListener(x);}public void onSubmit(ActionListener x){s.addActionListener(x);}}